import { Component } from '@angular/core';
import { CourseService } from './course.service';
import { OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';


interface Course {
  name: string;
  description: string;
  duration: string;
}
@Component({
  selector: 'app-enrolled-course',
  templateUrl: './enrolled-course.component.html',
  styleUrls: ['./enrolled-course.component.css'],
  providers: [CourseService]
})
export class EnrolledCourseComponent {
  enrolledCourses: Course[] = [];
  deletedCourses: Course[] = [];
  searchTerm: string = '';
  constructor(private courseService: CourseService) {
    this.enrolledCourses = this.courseService.getEnrolledCourses();
  }

  clearSearch(): void {
    this.searchTerm = '';
  }
  get filteredCourses(): Course[] {
    if (this.searchTerm.trim() === '') {
      return this.enrolledCourses;
    } else {
      const searchTermLower = this.searchTerm.toLowerCase();
      return this.enrolledCourses.filter(course =>
        course.name.toLowerCase().includes(searchTermLower)
      );
    }
  }
}
<h3>Enrolled Courses</h3>
<div class="text-center mt-3 search-bar-container">
  <div class="input-group">
    <input type="text" class="form-control search-bar" placeholder="Search Courses" [(ngModel)]="searchTerm">
    <div class="input-group-append">
      <button class="btn btn-danger" type="button" (click)="clearSearch()">
        <i class="fas fa-times"></i>
      </button>
    </div>
  </div>
</div>
  <br>
  <div class="row mb-2 text-center">
    <div class="col-md-6 px-4" *ngFor="let course of filteredCourses">
      <div class="card mb-4">
        <div class="card-body">
          <h3 class="mb-0 ">{{ course.name }}</h3>
          <p class="card-text mb-auto"><strong>Description:</strong> {{ course.description }}</p>
          <p class="card-text"><strong>Course Duration:</strong> {{ course.duration }}</p>
          <a href="#" class="btn btn-primary ml-2 btn-sm">Start Learning</a>
        </div>
      </div>
    </div>
  </div>

  <div class="row" *ngIf="filteredCourses.length === 0">
    <div class="col-md-12 text-center">
      <p>Sorry, No Course Found</p>
    </div>
  </div>
